import React, { useState } from 'react';
import '../index.css'; // Import the CSS file

const Products = () => {
  const [products, setProducts] = useState([
    // Add 100 entries of mock data
    // For simplicity, we are using generic data here
    { id: 1, name: 'Product A', category: 'Electronics', price: 100, stock: 50 },
    // ... Repeat this structure for more entries
  ]);

  const [newProductName, setNewProductName] = useState('');
  const [newProductCategory, setNewProductCategory] = useState('');
  const [newProductPrice, setNewProductPrice] = useState('');
  const [newProductStock, setNewProductStock] = useState('');

  const handleAddProduct = () => {
    const newProduct = {
      id: products.length + 1,
      name: newProductName || `Product ${products.length + 1}`,
      category: newProductCategory || 'Miscellaneous',
      price: parseFloat(newProductPrice) || 0,
      stock: parseInt(newProductStock) || 0,
    };

    setProducts([...products, newProduct]);

    // Reset the form fields
    setNewProductName('');
    setNewProductCategory('');
    setNewProductPrice('');
    setNewProductStock('');
  };

  const handleEditProduct = (productId, updatedProduct) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === productId ? { ...product, ...updatedProduct } : product
      )
    );
  };

  const handleDeleteProduct = (productId) => {
    setProducts((prevProducts) => prevProducts.filter((product) => product.id !== productId));
  };

  return (
    <div className="products-container">
      <h2>Products Management</h2>
      <div className="add-product-form">
        <input
          type="text"
          placeholder="Product Name"
          value={newProductName}
          onChange={(e) => setNewProductName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Category"
          value={newProductCategory}
          onChange={(e) => setNewProductCategory(e.target.value)}
        />
        <input
          type="number"
          placeholder="Price"
          value={newProductPrice}
          onChange={(e) => setNewProductPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Stock"
          value={newProductStock}
          onChange={(e) => setNewProductStock(e.target.value)}
        />
        <button className="add-button" onClick={handleAddProduct}>Add Product</button>
      </div>
      <ul className="products-list">
        {products.map((product) => (
          <li key={product.id} className="product-item">
            <strong className="product-name">{product.name}</strong> - Category: {product.category} - Price: ${product.price} - Stock: {product.stock}
            <button className="edit-button" onClick={() => handleEditProduct(product.id, { price: product.price + 10 })}>Edit</button>
            <button className="delete-button" onClick={() => handleDeleteProduct(product.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Products;
