import React, { useState } from 'react';
import './Products.css'; // Import the CSS file

const Products = () => {
  const [products, setProducts] = useState([
    // Add 100 entries of mock data
    // You can generate more meaningful mock data based on your needs
    // For simplicity, we are using generic data here
    { id: 1, name: 'Product A', category: 'Electronics', price: 100, stock: 50 },
    // ... Repeat this structure for more entries
  ]);

  const handleAddProduct = () => {
    const newProduct = {
      id: products.length + 1,
      name: `Product ${products.length + 1}`,
      category: 'Miscellaneous',
      price: Math.floor(Math.random() * 100) + 1, // Random price between 1 and 100
      stock: Math.floor(Math.random() * 50) + 1, // Random stock between 1 and 50
    };

    setProducts([...products, newProduct]);
  };

  const handleEditProduct = (productId, updatedProduct) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === productId ? { ...product, ...updatedProduct } : product
      )
    );
  };

  const handleDeleteProduct = (productId) => {
    setProducts((prevProducts) => prevProducts.filter((product) => product.id !== productId));
  };

  return (
    <div className="products-container">
      <h2>Products Management</h2>
      <button className="add-button" onClick={handleAddProduct}>Add Product</button>
      <ul className="products-list">
        {products.map((product) => (
          <li key={product.id} className="product-item">
            <strong className="product-name">{product.name}</strong> - Category: {product.category} - Price: ${product.price} - Stock: {product.stock}
            <button className="edit-button" onClick={() => handleEditProduct(product.id, { price: product.price + 10 })}>Edit</button>
            <button className="delete-button" onClick={() => handleDeleteProduct(product.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Products;
